CREATE TABLE IF NOT EXISTS Solution(SolutionID INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, Solution VARCHAR NOT NULL, Solved INT NOT NULL, Unsolved INT NOT NULL);
CREATE TABLE IF NOT EXISTS Language(Language VARCHAR NOT NULL, Regex VARCHAR NOT NULL, COUNT INT NOT NULL, PRIMARY KEY(Language));
CREATE TABLE IF NOT EXISTS Type(TypeID INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, Language VARCHAR NOT NULL, SolutionID INTEGER, TypeName VARCHAR NOT NULL, COUNT INT NOT NULL);
CREATE TABLE IF NOT EXISTS Errors(ErrorID INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, TypeID INTEGER NOT NULL, File VARCHAR, Line INT, MSG VARCHAR, Time DATETIME, COUNT INT NOT NULL);